function tags(e, t) {
    var n = e.split("<");
    for (var r = 0; r < n.length; r++) {
        if (n[r].indexOf(">") != -1) {
            n[r] = n[r].substring(n[r].indexOf(">") + 1, n[r].length)
        }
    }
    n = n.join("");
    n = n.substring(0, t - 1);
    return n
}
